mapper.select:
select rpNum, rpComment, rpWritername, rpWriternum, mvNum, rpDate, rpLike
from reviewreply
where mvNum = #{mvNum}

select rpNum, rpComment, rpWritername, rpWriternum, mvNum, rpDate, rpLike
from reviewreply
where rpWriternum = #{rpWriternum}

mapper.delete:
delete from reviewreply 
where rpNum = #{rpNum}
and rpWriternum = #{rpWriternum}
